import{_ as p,c as e,o as r,a as t}from"./app.d0234d09.js";const b='{"title":"\u5352\u696D","description":"","frontmatter":{},"headers":[],"relativePath":"Album/2-Tropic-of-Graduation/\u5352\u696D.md","lastUpdated":1661891206342}',a={},o=t('<h1 id="\u5352\u696D" tabindex="-1">\u5352\u696D <a class="header-anchor" href="#\u5352\u696D" aria-hidden="true">#</a></h1><p>\u4E2D\u8B6F: \u7562\u696D</p><p>\u4F5C\u8A5E\u30FB\u4F5C\u66F2: \u5C3E\u5D0E\u8C4A</p><p>\u7DE8\u66F2: \u897F\u672C\u660E</p><hr><p>\u6821\u820D\u7684\u9670\u5F71\u548C\u8349\u5730\u4E4B\u4E0A\u90A3\u50CF\u8981\u628A\u6211\u5438\u9032\u53BB\u7684\u5929\u7A7A</p><p>\u4EE4\u6211\u611F\u5230\u4E00\u7A2E\u5E7B\u8C61\u8207\u73FE\u5BE6\u4EA4\u7E54\u7684\u611F\u89BA</p><p>\u9418\u8072\u97FF\u8D77\uFF0C\u6211\u5750\u5728\u6559\u5BA4\u5167\u81EA\u5DF1\u5E73\u6642\u7684\u5EA7\u4F4D\u4E0A</p><p>\u60F3\u8457\u81EA\u5DF1\u5728\u9075\u5F9E\u751A\u9EBC\u4EE5\u53CA\u90A3\u662F\u5426\u61C9\u8A72\u8981\u9075\u5F9E</p><p>\u55A7\u9B27\u7684\u5FC3\uFF0C\u7531\u65BC\u89BA\u5F97</p><p>\u5982\u4ECA\u81EA\u5DF1\u6240\u64C1\u6709\u7684\u6771\u897F\u6BEB\u7121\u610F\u7FA9\u800C\u8FF7\u60D8</p><br><p>\u653E\u5B78\u5F8C\uFF0C\u5728\u8857\u4E0A\u904A\u8569\u7684\u6211\u5011</p><p>\u5728\u98A8\u4E2D\u5E36\u8457\u6D6E\u73FE\u51FA\u5B64\u7368\u7684\u773C\u795E\uFF0C\u5BC2\u5BDE\u5730\u8D70\u8457</p><p>\u5728\u5145\u6EFF\u4E86\u7B11\u8072\u548C\u5606\u606F\u7684\u5E97\u4E2D</p><p>\u6211\u5011\u5728\u73A9\u8457\u6CE2\u5B50\u6A5F\uFF0C\u6BD4\u8A66\u8AB0\u8F03\u9AD8\u5206</p><p>\u6C89\u60B6\u7684\u5FC3\uFF0C\u53EA\u8981\u627E\u5230\u523A\u6FC0</p><p>\u751A\u9EBC\u4E8B\u90FD\u8A87\u5927\u5730\u8AAA\u500B\u4E0D\u505C</p><br><p>\u751A\u9EBC\u5FAA\u898F\u8E48\u77E9\u4E00\u672C\u6B63\u7D93\uFF0C\u6211\u5011\u6839\u672C\u505A\u4E0D\u5230</p><p>\u5728\u6DF1\u591C\u7684\u6821\u5712\u88E1\uFF0C\u6211\u5011\u628A\u56DB\u5468\u7684\u73BB\u7483\u7A97\u6253\u7834</p><p>\u4E0D\u65B7\u53CD\u6297\uFF0C\u4E0D\u65B7\u6399\u624E\uFF0C\u5F88\u60F3\u5FEB\u4E00\u9EDE\u5F97\u5230\u81EA\u7531</p><p>\u5728\u8207\u7121\u6CD5\u4FE1\u4EFB\u7684\u5927\u4EBA\u7684\u6297\u722D\u4E2D</p><p>\u5C31\u7B97\u5F7C\u6B64\u539F\u8AD2\uFF0C\u5230\u5E95\u53C8\u80FD\u4E86\u89E3\u5230\u5C0D\u65B9\u4E9B\u751A\u9EBC</p><p>\u5728\u611F\u5230\u7169\u60B6\u7684\u540C\u6642\uFF0C\u4E5F\u5C31\u6B64\u5EA6\u904E</p><p>\u53EA\u660E\u767D\u4E86\u4E00\u6A23\u6771\u897F</p><p>\u5F9E\u9019\u500B\u652F\u914D\u4E2D\u7562\u696D</p><br><p>\u5927\u5BB6\u90FD\u5728\u71B1\u70C8\u5730\u8AC7\u8AD6\u8457\u6253\u67B6\u7684\u4E8B</p><p>\u60F3\u77E5\u9053\u81EA\u5DF1\u7A76\u7ADF\u6709\u591A\u5F37</p><p>\u9811\u56FA\u5730\u76F8\u4FE1\u53EA\u6709\u529B\u91CF\u662F\u5FC5\u8981\u7684</p><p>\u544A\u8AA1\u5225\u4EBA\u9075\u5F9E\u5C31\u4EE3\u8868\u8A8D\u8F38</p><p>\u5C31\u7B97\u5C0D\u8457\u670B\u53CB\uFF0C\u4E5F\u8981\u53BB\u901E\u5F37</p><p>\u96D6\u7136\u5E38\u5E38\u70BA\u6B64\u800C\u50B7\u5BB3\u4E86\u5225\u4EBA</p><br><p>\u5F88\u5FEB\u6240\u6709\u4EBA\u4E5F\u58AE\u5165\u611B\u6CB3\uFF0C</p><p>\u88AB\u611B\u7684\u8AAA\u8A71\u548C\u7406\u60F3\u7684\u611B\u8FF7\u4F4F\u4E86</p><p>\u96D6\u8AAA\u70BA\u4E86\u751F\u5B58\u4E0D\u5F97\u4E0D\u7CBE\u6253\u7D30\u7B97</p><p>\u4F46\u537B\u6DF1\u4FE1\u611B\u4EBA\u6642\u7684\u4E00\u5FC3\u4E00\u610F</p><p>\u7A76\u7ADF\u91CD\u8981\u7684\u662F\u751A\u9EBC\u5462\uFF1F\u662F\u611B\u9084\u662F\u751F\u5B58</p><p>\u7121\u6CD5\u5340\u5225\u800C\u611F\u5230\u8FF7\u5931</p><br><p>\u751A\u9EBC\u5FAA\u898F\u8E48\u77E9\u4E00\u672C\u6B63\u7D93\uFF0C\u7D66\u6211\u5403\u5C4E\u53BB\u5427</p><p>\u5728\u6DF1\u591C\u7684\u6821\u5712\u88E1\uFF0C\u6211\u5011\u628A\u56DB\u5468\u7684\u73BB\u7483\u7A97\u6253\u7834</p><p>\u4E0D\u65B7\u53CD\u6297\uFF0C\u4E0D\u65B7\u6399\u624E\uFF0C\u5F88\u60F3\u5FEB\u4E00\u9EDE\u5F97\u5230\u81EA\u7531</p><p>\u5728\u8207\u7121\u6CD5\u4FE1\u4EFB\u7684\u5927\u4EBA\u7684\u6297\u722D\u4E2D</p><p>\u5C31\u7B97\u5F7C\u6B64\u539F\u8AD2\uFF0C\u5230\u5E95\u53C8\u80FD\u4E86\u89E3\u5230\u5C0D\u65B9\u4E9B\u751A\u9EBC</p><p>\u5728\u611F\u5230\u7169\u60B6\u7684\u540C\u6642\uFF0C\u4E5F\u5C31\u6B64\u5EA6\u904E</p><p>\u53EA\u660E\u767D\u4E86\u4E00\u6A23\u6771\u897F</p><p>\u5F9E\u9019\u500B\u652F\u914D\u4E2D\u7562\u696D</p><br><p>\u5C31\u7B97\u7562\u696D\uFF0C\u6211\u53C8\u80FD\u660E\u767D\u5230\u4E9B\u751A\u9EBC</p><p>\u9664\u4E86\u56DE\u61B6\uFF0C\u6211\u53C8\u7559\u4E0B\u4E86\u4E9B\u751A\u9EBC</p><p>\u5047\u5982\u6240\u6709\u4EBA\u4E5F\u662F\u88AB\u675F\u7E1B\u7684\u8EDF\u5F31\u7F94\u7F8A\u7684\u8A71</p><p>\u8001\u5E2B\u4F60\u5C31\u662F\u8EDF\u5F31\u5927\u4EBA\u7684\u4EE3\u8A00\u4EBA\u55CE</p><p>\u6211\u5011\u7684\u6012\u61A4\u61C9\u8A72\u6307\u5411\u4F55\u8655\u554A</p><p>\u4ECA\u5F8C\u9084\u6709\u751A\u9EBC\u6703\u675F\u7E1B\u6211\u554A</p><p>\u9084\u8981\u5F9E\u81EA\u5DF1\u7562\u696D\u591A\u5C11\u6B21</p><p>\u624D\u80FD\u6210\u70BA\u771F\u6B63\u7684\u81EA\u5DF1\u554A</p><br><p>\u5728\u88AB\u8A2D\u8A08\u597D\u7684\u81EA\u7531\u4E2D\uFF0C\u6240\u6709\u4EBA\u4E5F\u4E0D\u4EE5\u70BA\u610F</p><p>\u6399\u624E\u7684\u65E5\u5B50\u4E5F\u7D42\u6703\u5B8C\u7D50</p><p>\u5F9E\u9019\u500B\u652F\u914D\u4E2D\u7562\u696D</p><br><p>\u5F9E\u9B25\u722D\u4E2D\u7562\u696D</p><hr><p>\u89E3\u8AAA\uFE55</p><p>\u539F\u672C\u5C0D\u65BC\u9019\u9996\u6B4C\uFF0C\u6211\u6709\u5F88\u591A\u60F3\u4ECB\u7D39\u7684\u6771\u897F\uFF0C\u53EF\u662F\uFF0C\u6700\u8FD1\u5FC3\u9748\u7E3D\u662F\u9677\u65BC\u4F4E\u6F6E\uFF0C\u7121\u6CD5\u597D\u597D\u53BB\u5BEB\u6587\u7AE0\u3002</p><p>\u8981\u8AAA\u539F\u56E0\u7684\u8A71\uFF0C\u5C31\u662F\u56E0\u70BA\u6211\u6F38\u6F38\u770B\u4E0D\u5230\u5922\u60F3\u5427\u3002\u6211\u5728\u554F\uFF0C\u6F38\u6F38\u88AB\u65E5\u5E38\u751F\u6D3B\u541E\u566C\uFF0C\u6F38\u6F38\u5931\u53BB\u751F\u6C23\u7684\u81EA\u5DF1\uFF0C\u7A76\u7ADF\u70BA\u81EA\u5DF1\u7684\u5922\u60F3\u5E79\u904E\u751A\u9EBC\u5462\uFF1F\u53E3\u4E2D\u8AAA\u8457\u5922\u60F3\uFF0C\u73FE\u5BE6\u4E2D\u537B\u751A\u9EBC\u90FD\u6C92\u505A\uFF0C\u90A3\u6A23\u7684\u751F\u6D3B\u771F\u7684\u8981\u7E7C\u7E8C\u4E0B\u53BB\u55CE\uFF1F\u6211\u5FFD\u7136\u5F88\u6015\uFF0C\u5F88\u6015\u5C31\u6B64\u865B\u5EA6\u4E00\u751F\uFF0C\u4F46\u662F\u5FC3\u9748\u537B\u89BA\u5F97\u7F3A\u4E4F\u529B\u91CF\uFF0C\u90A3\u662F\u56E0\u70BA\u5BC2\u5BDE\u7684\u751F\u6D3B\u904E\u5F97\u592A\u4E45\u4E86\u55CE\uFF1F\u4E00\u500B\u4E5F\u597D\uFF0C\u53EA\u8981\u8B93\u6211\u770B\u898B\u4E00\u500B\u70BA\u5922\u60F3\u800C\u596E\u9B25\u7684\u4EBA\uFF0C\u90A3\u9EBC\u6211\u5C31\u80FD\u518D\u6B21\u71C3\u8D77\u71B1\u60C5\u3002\u80E1\u4E82\u5BEB\u4E86\u9019\u4E00\u5806\u5B57\uFF0C\u771F\u7684\u6709\u9EDE\u4E0D\u77E5\u6240\u8B02\uFF0C\u5C31\u50CF\u6211\u7684\u751F\u6D3B\u4E00\u6A23\u3002</p><p>\u6211\u60F3\u898B\u5979\uFF0C\u771F\u7684\u2026\u2026</p><p>\u6211\u662F\u4E00\u500B\u5922\u904A\u8005\uFF0C\u4E00\u500B\u7368\u81EA\u8D70\u5728\u4EBA\u7FA4\u4E2D\u7684\u5922\u904A\u8005\u3002</p><hr><p>\u8F49\u8F09\u81EA:</p><ul><li><a href="https://mypaper.pchome.com.tw/forgetnot/post/1239735283" target="_blank" rel="noopener noreferrer">\u7562\u696D\uFF20\u666E\u901A\u7684\u611B\uFF5CPChome\u65B0\u805E\u53F0</a></li></ul>',74),_=[o];function n(s,c,d,i,h,l){return r(),e("div",null,_)}var m=p(a,[["render",n]]);export{b as __pageData,m as default};
