import{_ as e,c as a,o as r,a as t}from"./app.d0234d09.js";const I='{"title":"1984/08/04 - \u6771\u4EAC\xB7\u65E5\u6BD4\u8C37\u91CE\u5916\u97F3\u6A02\u5802","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u66F2\u76EE\uFE55","slug":"\u66F2\u76EE\uFE55"},{"level":3,"title":"\u5F8C\u8A71","slug":"\u5F8C\u8A71"}],"relativePath":"Live/19840804.md","lastUpdated":1678026033768}',l={},o=t('<h1 id="_1984-08-04-\u6771\u4EAC\xB7\u65E5\u6BD4\u8C37\u91CE\u5916\u97F3\u6A02\u5802" tabindex="-1">1984/08/04 - \u6771\u4EAC\xB7\u65E5\u6BD4\u8C37\u91CE\u5916\u97F3\u6A02\u5802 <a class="header-anchor" href="#_1984-08-04-\u6771\u4EAC\xB7\u65E5\u6BD4\u8C37\u91CE\u5916\u97F3\u6A02\u5802" aria-hidden="true">#</a></h1><p>\u9707\u3000\u64BC\u3000\u7684\u3000\u4E03\u3000\u7C73\u3000J\u3000U\u3000M\u3000P\u3000\uFF01\u3000\uFF01<br> ATOMIC CAFE MUSIC FESTIVAL &#39;84</p><p>\u7C21\u4ECB\uFE55<br> \u9019\u662F\u4E00\u500B\u4EE5\u53CD\u6838\u70BA\u4E3B\u984C\u7684\u6F14\u5531\u6703\uFF0C\u5171\u6709\u5341\u56DB\u7D44\u6B4C\u624B\u53C3\u52A0\uFF0C\u7576\u4E2D\u5305\u62EC\u767D\u9F8D\u3001\u52A0\u85E4\u767B\u7D00\u5B50\u3001ARB\u3001\u6700\u5F8C\u8868\u6F14\u7684\u662F\u6D5C\u7530\u7701\u543E\uFF0COZAKI\u662F\u5176\u4E2D\u4E4B\u4E00\uFF0C\u7B2C\u4E03\u500B\u51FA\u5834\u3002</p><hr><h3 id="\u66F2\u76EE\uFE55" tabindex="-1">\u66F2\u76EE\uFE55 <a class="header-anchor" href="#\u66F2\u76EE\uFE55" aria-hidden="true">#</a></h3><ol><li>\u53CD\u6838</li><li>Scrambling Rock&#39;n&#39; Roll</li><li>\u5341\u4E03\u6B73\u306E\u5730\u56F3</li><li>\u611B\u306E\u6D88\u3048\u305F\u8857</li></ol><hr><p>\u6700\u521D\u7684\u6B4C\u66F2\u662F\u300C\u53CD\u6838\u300D\uFF0C\u662F\u300C\u6838(CORE)\u300D\u7684\u539F\u66F2\u3002\u821E\u53F0\u7684\u5DE6\u908A\u6709\u4E00\u53F0\u92FC\u7434\uFF0COZAKI\u5F48\u8457\u7434\u5531\u8457\uFF0C\u5531\u5230\u6700\u5F8C\uFF0COZAKI\u8D70\u5230\u53F0\u4E2D\u5FC3\u5531\u300C\u64C1\u62B1\u6211\u5427\uFF01\u300D\u90A3\u4E00\u6BB5\u3002</p><p>\u7B2C\u4E8C\u9996\u662F\u300CScrambling Rock&#39;n&#39; Roll\u300D\uFF0C\u4ED6\u5728\u6F14\u5531\u524D\u7684MC\u8AAA\uFE55\u300C\u6709\u8A0E\u53AD\u7684\u4E8B\u4E5F\u597D\uFF0C\uFF5E\u7565\uFF5E\u6709\u6230\u722D\u4E5F\u597D\uFF0C\u6211\u4E5F\u5728\u5E79\u8457Rock&#39;n&#39; Roll\u554A\uFF01\u300D\u4E4B\u5F8C\u4FBF\u958B\u59CB\u6F14\u5531\u3002\u5728\u9593\u594F\u6642\uFF0COZAKI\u5FFD\u7136\u722C\u4E0A\u4E03\u7C73\u9AD8\u7684\u7167\u660E\u9435\u67B6\uFF0C\u624B\u5283\u5341\u5B57\u5F8C\u4FBF\u8DF3\u4E86\u4E0B\u4F86\u3002</p><p>\u5834\u5167\u4E00\u7247\u5629\u7136\uFF01\u5DE5\u4F5C\u4EBA\u54E1\u628AOZAKI\u62AC\u56DE\u5F8C\u53F0\uFF0C\u4F46\u97F3\u6A02\u6C92\u6709\u505C\u6B62\uFF0C\u7D04\u4E03\u5206\u9418\u5F8C\uFF0COZAKI\u88AB\u5DE5\u4F5C\u4EBA\u54E1\u625B\u8457\u56DE\u5230\u821E\u53F0\u3002\u4ED6\u958B\u59CB\u5531\u300C\u4F60\u4E0D\u60F3\u8B8A\u5F97\u81EA\u7531\u55CE\u300D\uFF0C\u4F46\u4E0D\u5230\u4E00\u5206\u9418\uFF0C\u4FBF\u7AD9\u4E0D\u4F4F\u5012\u4E0B\uFF0C\u96D6\u60F3\u7AD9\u8D77\u4F86\uFF0C\u537B\u7AD9\u4E0D\u5230\u3002(\u56E0\u70BA\u8173\u9AA8\u6298\u4E86)</p><p>\u4E4B\u5F8C\u5230\u7B2C\u4E09\u9996\u6B4C\uFF0COZAKI\u5728\u6F14\u5531\u524D\u7684MC\u8AAA\uFE55\u300C\u6211\u6703\u5531\u554A\uFF0C\u50B7\u5F97\u5F88\u91CD\u5462\uFF0C\u5927\u6982\u662F\u90A3\u500B\uFF08\u9AA8\u6298\uFF09\u554A\uFF01\u6709\u5F88\u591A\u8A0E\u53AD\u7684\u4E8B\uFF0C\u5728\u516D\u672C\u6728\u7684\u5341\u5B57\u8DEF\u53E3\u5403\u78EF\u908A\u71D2\u6642\uFF0C\u4E5F\u6709\u8A0E\u53AD\u7684\u4E8B\u554A\u3002\u5C31\u662F\u90A3\u6A23\u3002\u90A3\u7A2E\u8A0E\u53AD\u7684\u611F\u89BA\u8981\u600E\u6A23\u624D\u80FD\u4EE5\u9019Rock&#39;n&#39; Roll\u6D88\u89E3\u5462\uFF0C\u90A3\u662F\u5F88\u91CD\u8981\u7684\u5427\uFF5E\u3002\u96D6\u7136\u5728\u5BB6\u88E1\u8981\u606D\u606D\u656C\u656C\u4E5F\u5F88\u8F9B\u82E6\uFF0C\u4F46\u5404\u4F4D\uFF0CRock&#39;n&#39; Roll\u5C31\u662F\u4EBA\u751F\uFF01\u300D</p><p>OZAKI\u958B\u59CB\u6F14\u5531\uFF0C\u4ED6\u63E1\u8457\u7C73\u9AD8\u5CF0\u5012\u4E0B\uFF0C\u6B4C\u8072\u4E5F\u65B7\u65B7\u7E8C\u7E8C\u7684\uFF0C\u4E4B\u5F8C\u4ED6\u5750\u8D77\u4F86\u5531\uFF0C\u8868\u60C5\u76F8\u7576\u75DB\u82E6\u3002\u63A5\u8457\u4ED6\u53C8\u5012\u4E0B\uFF0C\u53EA\u6709\u526F\u6B4C\u8E72\u8457\u5531\uFF0C\u62F3\u982D\u7E3D\u662F\u5411\u4E0A\u8209\u3002\u5230\u4E86\u7B2C\u4E8C\u6BB5\uFF0C\u4ED6\u5750\u5728\u92FC\u7434\u4E0A\u5531\uFF0C\u76F4\u5230\u5531\u5B8C\u3002\u4E4B\u5F8C\uFF0C\u4ED6\u518D\u5531\u7562\u300C\u611B\u6D88\u5931\u4E86\u7684\u8857\u300D\uFF0C\u7136\u5F8C\u4FBF\u88AB\u62AC\u56DE\u5F8C\u53F0\uFF0C\u9001\u53BB\u91AB\u9662\u3002</p><hr><h3 id="\u5F8C\u8A71" tabindex="-1">\u5F8C\u8A71 <a class="header-anchor" href="#\u5F8C\u8A71" aria-hidden="true">#</a></h3><p>\u5F8C\u4F86\u8B49\u5BE6OZAKI\u5DE6\u8173\u8907\u96DC\u9AA8\u6298\uFF0C\u9700\u8981\u4E09\u500B\u6708\u624D\u53EF\u5B8C\u5168\u5EB7\u5FA9\u3002OZAKI\u539F\u672C\u9810\u5B9A\u5728\u7B2C\u4E8C\u5929\uFF0C\u540C\u6A23\u5728\u6B64\u91CE\u5916\u97F3\u6A02\u5802\u8207\u5409\u5DDD\u6643\u53F8\u548C\u5C0F\u5C71\u5353\u6CBB\u6709\u4E00\u500B\u5408\u6F14\uFF0C\u4F46\u7531\u65BC\u9AA8\u6298\u800C\u4E0D\u80FD\u51FA\u5E2D\uFF0C\u8207\u4ED6\u662F\u670B\u53CB\u7684\u5409\u5DDD\u6643\u53F8\u5728\u7FCC\u65E5\u7684\u5408\u6F14\u4E2D\u8AAA\uFE55\u300COZAKI\u90A3\u7B28\u86CB\uFF0C\u6211\u672C\u4F86\u662F\u5F88\u60F3\u8DDF\u4ED6\u540C\u53F0\u6F14\u51FA\u7684\u554A\uFF01\u300D\u800C\u539F\u672C\u9810\u5B9A\u4E4B\u5F8C\u5C55\u958B\u7684TOUR\uFF08\u7576\u4E2D\u5305\u62EC\u539F\u5B9A9\u67084\u65E5\u5728\u65E5\u672C\u9752\u5E74\u9928\u8209\u884C\u7684\u6F14\u5531\u6703\uFF09\u4EA6\u540C\u6A23\u56E0\u9AA8\u6298\u800C\u53D6\u6D88\uFF0C\u9019\u5C0D\u65BC\u4E00\u500B\u525B\u8D77\u6B65\u7684\u65B0\u4EBA\u4F86\u8AAA\uFF0C\u7121\u7591\u662F\u4E00\u500B\u83AB\u5927\u6253\u64CA\u3002\u4F46\u4ED6\u9019\u50B3\u8AAA\u822C\u7684\u6F14\u51FA\uFF0C\u537B\u6DF1\u6DF1\u523B\u5165\u4EBA\u5011\u7684\u8166\u5B50\u88E1\u3002</p><p>OZAKI\u5728\u8A2A\u554F\u4E2D\u8AC7\u8D77\u90A3\u6642\u7684\u4E8B\uFF0C\u4ED6\u8AAA\u4ED6\u5728\u4E4B\u524D\u7576\u767D\u4E95\u8CB4\u5B50\u7684\u958B\u5834\u8868\u6F14\u5609\u8CD3\u6642\uFF0C\u89C0\u773E\u53EA\u662F\u5750\u8457\u51B7\u6DE1\u5730\u770B\u8457\uFF0C\u65BC\u662F\u4ED6\u5FC3\u60F3\u9019\u5929\u4E5F\u6703\u662F\u540C\u6A23\u60C5\u6CC1\uFF0C\u4FBF\u5E36\u8457\u300C\u4F60\u5011\u9019\u7FA4\u6DF7\u86CB\uFF0C\u807D\u6211\u5531\u554A\uFF01\u300D\u9019\u7A2E\u5FC3\u614B\u51FA\u4F86\u6F14\u5531\u3002\u600E\u6599\u4ED6\u4E00\u51FA\u5834\uFF0C\u4FBF\u6709\u7D04100\u540D\u89C0\u773E\u6E67\u5230\u53F0\u524D\uFF0C\u4ED6\u70BA\u6B64\u611F\u5230\u9A5A\u8A1D\uFF0C\u611F\u89BA\u5230\u81EA\u5DF1\u7684\u9810\u8A08\u8207\u89C0\u773E\u7684\u53CD\u61C9\u7684\u8DDD\u96E2\uFF0C\u70BA\u4E86\u63A9\u84CB\u6B64\u4E00\u9EDE\uFF0C\u4E5F\u7576\u662F\u7B54\u8B1D\u89C0\u773E\uFF0C\u4ED6\u5FD8\u6211\u5730\u6F14\u51FA\u3002\u7576\u4ED6\u722C\u4E0A\u4E86\u7167\u660E\u67B6\uFF0C\u6709\u89C0\u773E\u4E0D\u65B7\u53EB\uFE55\u300C\u5FEB\u9EDE\u8DF3\u4E0B\u4F86\u5427\uFF01\u300D\u800C\u525B\u5DE7\u9593\u594F\u97F3\u6A02\u4E5F\u5B8C\u4E86\uFF0C\u65BC\u662F\u4ED6\u4FBF\u8DF3\u4E0B\u4F86\u4E86\u3002\u4E0D\u904E\uFF0C\u4ED6\u8AAA\u4ED6\u5728\u8DF3\u4E4B\u524D\u6C92\u60F3\u5230\u6703\u9AA8\u6298\u5462\uFF01(\u7B11)</p><hr><p>\u8F49\u8F09\u81EA:</p><ul><li><a href="https://web.archive.org/web/20110501074524/http://blog.yam.com/forgetnot/category/634985/page=4" target="_blank" rel="noopener noreferrer">1984\u5E748\u67084\u65E5 \u65E5\u6BD4\u8C37 (\u50B3\u8AAA\u7684\u4E03\u7C73JUMP)</a></li></ul>',19),i=[o];function n(p,c,h,_,d,s){return r(),a("div",null,i)}var O=e(l,[["render",n]]);export{I as __pageData,O as default};
