import{_ as r,c as e,o as b,a as l}from"./app.d0234d09.js";const _=`{"title":"1985/08/25 - \u5927\u962A\u7403\u5834","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u66F2\u76EE\uFE55","slug":"\u66F2\u76EE\uFE55"},{"level":3,"title":"\u30C9\u30FC\u30CA\u30C4\u30FB\u30B7\u30E7\u30C3\u30D7","slug":"\u30C9\u30FC\u30CA\u30C4\u30FB\u30B7\u30E7\u30C3\u30D7"},{"level":3,"title":"\u5352\u696D","slug":"\u5352\u696D"},{"level":3,"title":"\u5742\u306E\u4E0B\u306B\u898B\u3048\u305F\u3042\u306E\u8857\u306B","slug":"\u5742\u306E\u4E0B\u306B\u898B\u3048\u305F\u3042\u306E\u8857\u306B"},{"level":3,"title":"Scrap Alley","slug":"scrap-alley"},{"level":3,"title":"\u30CF\u30A4\u30B9\u30AF\u30FC\u30EB\u3000Rock'n'Roll","slug":"\u30CF\u30A4\u30B9\u30AF\u30FC\u30EB-rock-n-roll"},{"level":3,"title":"Freeze Moon","slug":"freeze-moon"}],"relativePath":"Live/19850825.md","lastUpdated":1650762966064}`,a={},o=l('<h1 id="_1985-08-25-\u5927\u962A\u7403\u5834" tabindex="-1">1985/08/25 - \u5927\u962A\u7403\u5834 <a class="header-anchor" href="#_1985-08-25-\u5927\u962A\u7403\u5834" aria-hidden="true">#</a></h1><p>\u5728\u4E8C\u842C\u591A\u4EBA\u9762\u524D\u71C3\u71D2\u7684\u5341\u4E5D\u6B72\u4E4B\u590F</p><hr><h3 id="\u66F2\u76EE\uFE55" tabindex="-1">\u66F2\u76EE\uFE55 <a class="header-anchor" href="#\u66F2\u76EE\uFE55" aria-hidden="true">#</a></h3><ol><li>\u7C73\u8ECD\u30AD\u30E3\u30F3\u30D7</li><li>Driving All Night</li><li>\u306F\u3058\u307E\u308A\u3055\u3048\u6B4C\u3048\u306A\u3044</li><li>Bow!</li><li>\u8857\u306E\u98A8\u666F</li><li>\u30C9\u30FC\u30CA\u30C4\u30FB\u30B7\u30E7\u30C3\u30D7</li><li>\u30C0\u30F3\u30B9\u30DB\u30FC\u30EB</li><li>\u5352\u696D</li><li>\u5742\u306E\u4E0B\u306B\u898B\u3048\u305F\u3042\u306E\u8857\u306B</li><li>Scrap Alley</li><li>\u5B58\u5728</li><li>\u30CF\u30A4\u30B9\u30AF\u30FC\u30EB Rock&#39;n&#39;Roll</li><li>Scrambling Rock&#39;n&#39;Roll</li><li>\u5341\u4E03\u6B73\u306E\u5730\u56F3</li><li>\u611B\u306E\u6D88\u3048\u305F\u8857</li></ol><p>-ENCORE-</p><ol start="16"><li>Freeze Moon</li><li>I LOVE YOU</li><li>\u30B7\u30A7\u30EA\u30FC</li></ol><hr><h3 id="\u30C9\u30FC\u30CA\u30C4\u30FB\u30B7\u30E7\u30C3\u30D7" tabindex="-1">\u30C9\u30FC\u30CA\u30C4\u30FB\u30B7\u30E7\u30C3\u30D7 <a class="header-anchor" href="#\u30C9\u30FC\u30CA\u30C4\u30FB\u30B7\u30E7\u30C3\u30D7" aria-hidden="true">#</a></h3><blockquote><p>\u5915\u66AE\u308C\u306E\u753A\u3092\u50D5\u306F\u6B7B\u3092\u80CC\u8CA0\u3063\u305F\u4E00\u5339\u306E\u5C0F\u3055\u306A\u866B\u306B\u306A\u3063\u3066\u30FB\u30FB\u30FB<br> \u50D5\u304C\u3044\u3064\u304B\u601D\u3044\u51FA\u3092\u6B8B\u3057\u3066\u304D\u305F\u5834\u6240\u3078\u3001\u601D\u3044\u3092\u306F\u305B\u3066\u3044\u308B\u3002<br> \u3057\u3070\u3089\u304F\u6B69\u3044\u3066\u30FB\u30FB\u5742\u9053\u306E\u9014\u4E2D\u306E\u30AC\u30FC\u30C9\u30EC\u30FC\u30EB\u306E\u8107\u306B\u3001\u50D5\u306F\u8170\u3092\u4E0B\u308D\u3057\u305F\u3002\u305D\u3057\u3066\u3001\u50D5\u4EE5\u5916\u306E\u8AB0\u304B\u3068\u4F55\u304B\u3092\u5206\u304B\u3061\u5408\u3046\u305F\u3081\u306B\u76EE\u3092\u9589\u3058\u3066\u3044\u305F\u3093\u3060\u3002<br> \u3053\u306E\u753A\u306E\u4E2D\u3067\u306F\u3001\u50D5\u4EE5\u5916\u306E\u4F55\u3082\u304B\u3082\u304C\u3001\u50D5\u3092\u5426\u5B9A\u3057\u3088\u3046\u3068\u3057\u3066\u3044\u308B\u3002\u3000\u50D5\u306F\u3082\u3046\u30C0\u30E1\u304B\u3082\u77E5\u308C\u306A\u3044\u3068\u601D\u3063\u305F\u3093\u3060\u3002\u3000\u305D\u3093\u306A\u3068\u304D\u3001\u50D5\u306E\u53F3\u982C\u3092\u7167\u3089\u3059\u5C0F\u3055\u306A\u660E\u304B\u308A\u3092\u898B\u3064\u3051\u305F\u3002<br> \u3061\u3087\u3046\u3069\u50D5\u306E\u5EA7\u3063\u3066\u3044\u308B\u5834\u6240\u304B\u3089\u3000\u3068\u3063\u3066\u3082\u7DBA\u9E97\u306A\u5915\u65E5\u304C\u3001\u30D3\u30EB\u3068\u30D3\u30EB\u3068\u306E\u5408\u9593\u306B\u3001\u6C88\u3093\u3067\u3044\u304F\u3068\u3053\u308D\u3060\u3063\u305F\u3093\u3060\u30FB\u30FB\u30FB\u30FB\u3002</p></blockquote><p>\u5728\u508D\u665A\u7684\u57CE\u5E02\uFF0C\u6211\u8B8A\u6210\u4E86\u4E00\u689D\u80CC\u8CA0\u8457\u6B7B\u4EA1\u7684\u87F2\u5B50\u2026\u2026<br> \u6211\u524D\u5F80\u6211\u66FE\u5E7E\u4F55\u6642\u7559\u4E0B\u904E\u56DE\u61B6\u7684\u5730\u65B9\uFF0C\u6211\u7684\u601D\u7DD2\u5728\u5954\u8D70\u3002<br> \u8D70\u4E86\u4E00\u6703\u5152\u2026\u2026\u6211\u5728\u659C\u5761\u9014\u4E2D\u7684\u9632\u649E\u6B04\u65C1\u5750\u4E86\u4E0B\u4F86\u3002<br> \u7136\u5F8C\uFF0C\u70BA\u4E86\u8207\u5176\u4ED6\u4EBA\u5206\u4EAB\u67D0\u4E9B\u6771\u897F\uFF0C\u6211\u9589\u4E0A\u4E86\u773C\u3002<br> \u5728\u9019\u500B\u57CE\u5E02\u4E2D\uFF0C\u6211\u4EE5\u5916\u7684\u4E00\u5207\uFF0C\u90FD\u6B63\u5728\u4E0D\u65B7\u5730\u5426\u5B9A\u6211\u3002<br> \u6211\u60F3\uFF0C\u6211\u4E5F\u8A31\u5DF2\u7D93\u4E0D\u884C\u4E86\u3002<br> \u5C31\u5728\u90A3\u6642\u5019\uFF0C\u6211\u767C\u73FE\u6709\u5EA6\u5FAE\u5F31\u7684\u5149\u7167\u8457\u6211\u7684\u53F3\u908A\u81C9\u9830\u3002<br> \u6211\u6240\u5750\u7684\u5730\u65B9\uFF0C\u6B63\u597D\u80FD\u770B\u5230\u975E\u5E38\u7F8E\u9E97\u7684\u5915\u967D\uFF0C\u5728\u5927\u5EC8\u8207\u5927\u5EC8\u4E4B\u9593\uFF0C\u9010\u6F38\u5730\u843D\u4E0B\u2026\u2026</p><br><h3 id="\u5352\u696D" tabindex="-1">\u5352\u696D <a class="header-anchor" href="#\u5352\u696D" aria-hidden="true">#</a></h3><blockquote><p>\u4ECA\u65E5\u307E\u3067\u306B\u30FB\u30FB\u30FB\u30FB\u4ECA\u65E5\u3067\u4FFA\u306E\u30C4\u30A2\u30FC\u3082\u7D42\u308F\u308B\u3093\u3060\u3051\u308C\u3069\u3001<br> \u305D\u306E\u9593\u306B\u30FB\u30FB\u30FB\u30FB\u4FFA\u3082\u542B\u3081\u3066\u3001\u4FFA\u3084\u3001\u30E1\u30F3\u30D0\u30FC\u3084\u3001\u30B9\u30BF\u30C3\u30D5\u3084\u3001<br> \u4FFA\u305F\u3061\u3082\u4ECA\u65E5\u3053\u3053\u307E\u3067\u6765\u308B\u306E\u306B<br> \u307F\u3093\u306A\u6D99\u3092\u6D41\u3057\u3066\u304D\u305F\u3002<br> \u4FFA\u304C\u5B66\u6821\u3092\u8F9E\u3081\u305F\u3068\u304D\u3082\u3001\u53CB\u9054\u3068\u307F\u3093\u306A\u304C\u96C6\u307E\u3063\u3066\u30FB\u30FB\u30FB\u30FB<br> \u4FFA\u304C\u8F9E\u3081\u3066\u304B\u3089\u4FFA\u306E\u53CB\u9054\u304C\u7D9A\u3051\u3066\uFF13\u4EBA\u3082\u5B66\u6821\u3092\u8F9E\u3081\u3066\u3057\u307E\u3063\u305F\u30FB\u30FB\u30FB\u30FB\u3002<br> \u305D\u3044\u3064\u3089\u4E00\u4EBA\u4E00\u4EBA\u304C\u8F9E\u3081\u308B\u305F\u3073\u306B\u4FFA\u305F\u3061\u306F\u96C6\u307E\u3063\u3066\u3001<br> \u305D\u3044\u3064\u306F\u51C4\u304F\u8EDF\u6D3E\u306A\u3084\u3064\u3067\u3001\u307E\u3042\u3001\u65E5\u9803\u5922\u3060\u3068\u304B\u305D\u3093\u306A\u8A00\u8449\u3092\u53E3\u3092\u3057\u306A\u3044\u3068\u601D\u3046\u3002<br> \u3060\u3051\u3069\u6D99\u3092\u6D41\u3057\u306A\u304C\u3089 \u5922\u3084\u3001\u3053\u308C\u304B\u3089\u81EA\u5206\u304C\u9032\u3093\u3067\u884C\u304F\u30FB\u30FB\u3079\u304D\u9053\u3092\u71B1\u304F\u8A9E\u3063\u3066\u3044\u305F\u3002 \u3044\u3064\u3082\u6D99\u3092\u6D41\u3059\u3068\u304D\u3001\u3053\u306E\u6D99\u304C\u679C\u305F\u3057\u3066\u5E0C\u671B\u306E\u6D99\u306A\u306E\u304B\u3001<br> \u305D\u308C\u3068\u3082\u4F55\u304B\u3092\u5F8C\u6094\u3057\u305F\u6D99\u306A\u306E\u304B\u3063\u3066\u3001\u81EA\u5206\u306B\u554F\u3044\u304B\u3051\u3066\u307F\u308B\u3002\u3000<br> \u591A\u5206\u3001\u4E21\u65B9\u306A\u306E\u304B\u3082\u77E5\u308C\u306A\u3044\u3051\u308C\u3069\u30FB\u30FB\u30FB\u30FB<br> \u4ED6\u4EBA\u304C\u6D99\u3092\u6D41\u3057\u3066\u3044\u308B\u306E\u3092\u898B\u3066\u3001\u4F55\u304B\u8A00\u3063\u3066\u3084\u308C\u308B\u307B\u3069\u30FB\u30FB<br> \u81EA\u5206\u306F\u3044\u3044\u52A0\u6E1B\u306B\u306A\u308A\u305F\u304F\u306A\u3044\u3057\u3001<br> \u305F\u3060\u3001\u5FC3\u304B\u3089\u9858\u3046\u306E\u306F\u3001\u6D41\u3057\u305F\u6D99\u304C\u305D\u3044\u3064\u306E\u4EBA\u751F\u306B\u3068\u3063\u3066\u3001<br> \u3044\u3064\u304B\u5FC5\u305A\u7B11\u3063\u3066\u3057\u3083\u3079\u308C\u308B\u3088\u3046\u306B\u3001\u5831\u308F\u308C\u308B\u6D99\u306B\u306A\u3063\u3066\u304F\u308C\u308B\u30FB\u30FB\u30FB\u3053\u3068\u3092\u7948\u3063\u3066\u3044\u308B\u3002</p></blockquote><p>\u4F86\u5230\u4ECA\u5929\u2026\u2026\u96D6\u7136\u4ECA\u5929\u6211\u7684TOUR\u5C07\u6703\u5B8C\u7D50\uFF0C<br> \u4F46\u5728\u4E0D\u4E45\u524D\u2026\u2026\u5305\u62EC\u6211\u5728\u5167\uFF0C\u6211\u3001\u6A02\u968A\u6210\u54E1\u548C\u5DE5\u4F5C\u4EBA\u54E1\uFF0C<br> \u5168\u5230\u70BA\u80FD\u5920\u4F86\u5230\u4ECA\u5929\u800C\u6D41\u4E0B\u773C\u6DDA\u3002<br> \u5728\u6211\u9000\u5B78\u7684\u6642\u5019\uFF0C\u6211\u4E5F\u8DDF\u670B\u53CB\u805A\u5728\u4E00\u8D77\u2026\u2026<br> \u6211\u6709\u4E09\u500B\u670B\u53CB\u4E5F\u5728\u6211\u9000\u5B78\u4E4B\u5F8C\u9678\u7E8C\u9000\u5B78\u4E86\u2026\u2026\u3002<br> \u6BCF\u7576\u4ED6\u5011\u7576\u4E2D\u6709\u4EBA\u9000\u5B78\uFF0C\u6211\u5011\u4FBF\u6703\u51FA\u4F86\u805A\u6703\uFF0C<br> \u90A3\u50A2\u4F19\u662F\u4E00\u500B\u8F15\u6D6E\u7684\u4EBA\uFF0C\u5728\u5E73\u6642\u4ED6\u662F\u4E0D\u6703\u8AAA\u5922\u60F3\u4E4B\u985E\u7684\u8A71\u7684\u3002<br> \u53EF\u662F\uFF0C\u90A3\u6642\u4ED6\u4E00\u908A\u6D41\u8457\u6DDA\uFF0C\u4E00\u908A\u6FC0\u52D5\u5730\u8AAA\u8457\u5922\u60F3\u548C\u4ECA\u5F8C\u81EA\u5DF1\u61C9\u8D70\u7684\u8DEF\u3002<br> \u6BCF\u7576\u6D41\u6DDA\uFF0C\u6211\u7E3D\u6703\u8A66\u8457\u554F\u81EA\u5DF1\u9019\u4E9B\u773C\u6DDA\u5230\u5E95\u662F\u5E0C\u671B\u4E4B\u6DDA\uFF0C\u9084\u662F\u70BA\u751A\u9EBC\u800C\u5F8C\u6094\u7684\u6DDA\u3002<br> \u5927\u6982\uFF0C\u5169\u8005\u4E5F\u53EF\u80FD\u662F\u5427\u2026\u2026<br> \u53EF\u662F\uFF0C\u5728\u671B\u8457\u4ED6\u4EBA\u6D41\u6DDA\u6642\uFF0C<br> \u6211\u5E0C\u671B\u81EA\u5DF1\u80FD\u8AAA\u9EDE\u751A\u9EBC\u2026\u2026\u6211\u4E0D\u60F3\u5F97\u904E\u4E14\u904E\u3002<br> \u6211\u53EA\u662F\u5E0C\u671B\u6D41\u904E\u7684\u773C\u6DDA\uFF0C\u5728\u67D0\u5929\u80FD\u5C0D\u90A3\u50A2\u4F19\u7684\u4EBA\u751F\u800C\u8A00\uFF0C\u662F\u53EF\u4EE5\u7B11\u8457\u8AC7\u8D77\u7684\uFF0C\u5F97\u5230\u56DE\u5831\u7684\u773C\u6DDA\u2026\u2026</p><br><h3 id="\u5742\u306E\u4E0B\u306B\u898B\u3048\u305F\u3042\u306E\u8857\u306B" tabindex="-1">\u5742\u306E\u4E0B\u306B\u898B\u3048\u305F\u3042\u306E\u8857\u306B <a class="header-anchor" href="#\u5742\u306E\u4E0B\u306B\u898B\u3048\u305F\u3042\u306E\u8857\u306B" aria-hidden="true">#</a></h3><blockquote><p>\u4E00\u6628\u65E5\u306E\u591C\u4FFA\u306F\u3001\u5B9F\u5BB6\u306B\u5E30\u3063\u3066\u3001\u591C\u4E2D\u5B9F\u5BB6\u306B\u5E30\u3063\u305F\u3093\u3060\u3002\u30B5\u30E9\u30EA\u30FC\u30DE\u30F3\u306E\u5144\u8CB4\u3068\u89AA\u7236\u306F\u3001\u590F\u4F11\u307F\u306A\u3093\u3066\u3042\u308B\u308F\u3051\u3067\u3082\u306A\u304F\u3066\u3001\u305D\u308C\u3067\u3082\u305F\u307E\u305F\u307E\u4F11\u307F\u304C\u4E00\u7DD2\u306B\u306A\u3063\u3066<br> \u89AA\u7236\u3068\u5144\u8CB4\u306F\u3061\u3087\u3046\u3069\u591C\u4E2D\u307E\u3067\u305A\u3063\u3068\u591C\u901A\u3057\u98F2\u3093\u3067\u3044\u305F\u3002\u89AA\u7236\u306F\u3001\u3084\u304C\u3066\u63A7\u3048\u305F\u5B9A\u5E74\u306E\u8A71\u3092\u3057\u3001\u5144\u8CB4\u306F\u3082\u3046\u3059\u3050\u7D50\u5A5A\u3059\u308B\u3002\u305D\u3057\u3066\u4FFA\u306F\u5144\u8CB4\u306B\u300C\u6700\u8FD1\u3001\u3046\u3061\u306E\u65B9\u306F\u3069\u3046\u3060\uFF1F\u300D<br> \u305D\u3046\u805E\u3044\u305F\u3002\u3000\u305D\u3057\u305F\u3089\u3001\u671D\u304C\u6765\u3066\u3001\u591C\u304C\u6765\u3066\u3001\u305F\u3060\u305D\u308C\u3060\u3051\u3060\u3063\u3066\u30FB\u30FB\u30FB\u305D\u3093\u306A\u753A\u306E\u4E2D\u306B\u3082\u4FFA\u306E\u601D\u3044\u51FA\u306F\u305F\u304F\u3055\u3093\u3042\u308B\u3093\u3060\u30FB\u30FB\u30FB\u30FB\u3002<br> \u30EF\u30F3\u3000\u30C8\u30A5\u30FC\u3000\u30B9\u30EA\u30FC\u3000\u30D5\u30A9\u30FC\uFF01\uFF01</p></blockquote><p>\u524D\u5929\u665A\u4E0A\uFF0C\u6211\u56DE\u5230\u4E86\u8001\u5BB6\uFF0C\u5728\u6DF1\u591C\u56DE\u53BB\u7684\u3002<br> \u8981\u5DE5\u4F5C\u7684\u54E5\u54E5\u548C\u7238\u7238\u96D6\u7136\u4E0D\u6703\u6709\u6691\u5047\uFF0C\u4F46\u6B63\u597D\u5927\u5BB6\u90FD\u653E\u5047\uFF0C\u4FBF\u4E00\u8D77\u6E34\u9152\u5230\u6DF1\u591C\u3002<br> \u7238\u7238\u8AAA\u4ED6\u5FEB\u8981\u9000\u4F11\uFF0C\u54E5\u54E5\u4E5F\u5FEB\u8981\u7D50\u5A5A\u4E86\u3002<br> \u7136\u5F8C\u6211\u554F\u54E5\u54E5\uFE55\u300C\u6700\u8FD1\u5BB6\u88E1\u600E\u6A23\uFF1F\u300D<br> \u4ED6\u8AAA\uFE55\u300C\u65E5\u51FA\u65E5\u843D\uFF0C\u5C31\u53EA\u662F\u9019\u6A23\u2026\u2026\u300D<br> \u5728\u90A3\u57CE\u5E02\u4E2D\uFF0C\u6211\u6709\u8457\u5F88\u591A\u7684\u56DE\u61B6\u2026\u2026\u3002<br> ONE TWO THREE FOUR!</p><br><h3 id="scrap-alley" tabindex="-1">Scrap Alley <a class="header-anchor" href="#scrap-alley" aria-hidden="true">#</a></h3><blockquote><p>\u53BB\u5E74\u306E\u590F\u306F\u3001\u4FFA\u306F\u3042\u3093\u306A\u9AD8\u3044\u3068\u3053\u308D\u304B\u3089\u98DB\u3073\u964D\u308A\u3066\u5DE6\u8DB3\u3092\u9AA8\u6298\u3057\u3066\u3044\u305F\u3093\u3060\u3002<br> \u305D\u308C\u3067\u4FFA\u306F\u3001\u3084\u308B\u3053\u3068\u3082\u3067\u304D\u305A\u3044\u3064\u3082\u5BB6\u3067\u3001<br> \u30C8\u30A4\u30EC\u306B\u5165\u308B\u3068\u304D\u3082\u677E\u8449\u6756\u3067\u884C\u304B\u306A\u304F\u3061\u3083\u306A\u3089\u306A\u304B\u3063\u305F\u3093\u3060\u3002<br> \u305D\u308C\u3067\u3082\u591C\u4E2D\u306B\u306A\u308B\u3068\u4FFA\u306E\u53CB\u9054\u304C\u8ECA\u3067\u8FCE\u3048\u306B\u6765\u3066\u3001\u591C\u306E\u753A\u3092\u5F98\u5F8A\u3055\u305B\u3066\u304F\u308C\u305F\u3002<br> \u3060\u3051\u3069\u677E\u8449\u6756\u3067\u5F7C\u5973\u306B\u58F0\u3092\u304B\u3051\u3066\u3082\u8AB0\u3082\u76F8\u624B\u306B\u3057\u3066\u304F\u308C\u306A\u304B\u3063\u305F\u3002<br> \u305D\u3093\u306A\u3042\u308B\u65E5\u4FFA\u306F\u3044\u3064\u3082\u306E\u3088\u3046\u306B\u671D\u5E30\u3063\u3066\u3001\u663C\u9593\u76EE\u3092\u899A\u307E\u3057\u305F\u3001<br> \u305D\u3093\u306A\u3042\u308B\u65E5\u306E\u3053\u3068\u30FB\u30FB\uFF13\u5E74\u3076\u308A\u306B\u4FFA\u306E\u53CB\u9054\u304C\u8A2A\u306D\u3066\u304D\u305F\u3093\u3060\u3002\u3000<br> \u305D\u3044\u3064\u306F\u4FFA\u306B\u4F1A\u3046\u306A\u308A\u3053\u3046\u8A00\u3063\u305F\u3002<br> \u300C\u5C3E\u5D0E\u3001\u304A\u524D\u3082\u5909\u308F\u3063\u3066\u306D\u3047\u306A\u3002\u300D\u3000<br> \u305D\u3044\u3064\u306E\u3001\u305D\u3044\u3064\u306E\u6614\u3063\u3066\u8A00\u3046\u3068\u3001\u6614\u306F\u66B4\u8D70\u65CF\u3092\u3084\u3063\u3066\u3044\u3066\u982D\u306F\u30DE\u30C3\u30AD\u30C3\u30AD\u30F3\u3060\u3063\u305F\u3051\u308C\u3069\u3001<br> \u4ECA\u3058\u3083\u3001\u3093\uFF5E\u30FB\u30FB\u3064\u3044\u6700\u8FD1\u307E\u3067\u307E\u3060\u9AEA\u306E\u6BDB\u3092\u67D3\u3081\u3066\u3044\u305F\u3089\u3057\u304F\u3066\u3001<br> \u3061\u3087\u3063\u3068\u30FB\u30FB\u30FB\u9AEA\u306E\u751F\u3048\u969B\u306F\u307E\u3060\u8336\u8272\u304B\u3063\u305F\u3051\u308C\u3069\u3001\u3042\u3068\u306F\u3060\u3093\u3060\u3093\u9ED2\u304F\u306A\u308A\u304B\u3051\u3066\u3044\u305F\u3002<br> \u305D\u3044\u3064\u304C\u307E\u305F\u4FFA\u306B\u8A00\u3063\u305F\u3000<br> \u300C\u4FFA\u306F\u3088\u3049\u3001\u5B9F\u306F\u89AA\u7236\u306B\u306A\u308B\u3093\u3060\u30FB\u30FB\u30FB\u30FB\u3082\u3046\u3001\u6614\u307F\u305F\u3044\u306B\u60AA\u3044\u3053\u3068\u3082\u51FA\u6765\u3084\u3057\u306D\u3047\u3002<br> \u5C3E\u5D0E\u307F\u305F\u3044\u306B\u5922\u3084\u611B\u3060\u3068\u304B\u8A00\u3063\u3066\u3089\u3093\u306D\u3047\u3051\u3069\u30FB\u30FB<br> \u307E\u3042\u3001\u306A\u3093\u3066\u3063\u305F\u3063\u3066\u4FFA\u306B\u306F\u5BB6\u5EAD\u3063\u3066\u3082\u3093\u304C\u3042\u308B\u304B\u3089\u306A\u300D<br> \u305D\u3046\u4FFA\u306B\u8A00\u3063\u305F\u3093\u3060\u3002\u3060\u304B\u3089\u4FFA\u306F\u8A00\u3063\u3066\u3084\u3063\u305F\u3000<br> \u300C\u308F\u304B\u3063\u305F\u3001\u304A\u524D\u3082\u4FFA\u3082\u81EA\u5206\u3089\u3057\u304F\u751F\u304D\u3088\u3046\u30FB\u30FB\u81EA\u5206\u3089\u3057\u304F\u751F\u304D\u308B\u3053\u3068\u304C\u304D\u3063\u3068\u4E00\u756A\u7D20\u6575\u306A\u3093\u3060\u3002<br> \u304A\u3081\u3067\u3068\u3046\u3001\u305D\u3057\u3066\u30FB\u30FB\u30FB\u30FB\u30FB\u30FBSay goodbye Scrap Alley\u30FB\u30FB\u30FB\u30FB\u30FB\u30FB\u300D<br> \u30EF\u30F3\u30C8\u30A5\u30FC\u30B9\u30EA\u30FC\u30D5\u30A9\u30FC\uFF01</p></blockquote><p>\u53BB\u5E74\u590F\u5929\uFF0C\u6211\u5F9E\u90A3\u9EBC\u9AD8\u7684\u5730\u65B9\u8DF3\u4E0B\u4F86\uFF0C\u5DE6\u817F\u9AA8\u6298\u4E86\u3002<br> \u56E0\u6B64\uFF0C\u6211\u7121\u6CD5\u505A\u4E8B\uFF0C\u7D93\u5E38\u5728\u5BB6\u4E2D\uFF0C\u9023\u53BB\u5EC1\u6240\u4E5F\u5FC5\u9808\u8981\u7528\u67FA\u6756\u3002<br> \u5118\u7BA1\u5982\u6B64\uFF0C\u4E00\u5165\u591C\uFF0C\u6211\u7684\u670B\u53CB\u5C31\u99D5\u8ECA\u4F86\u63A5\u6211\uFF0C\u5230\u591C\u665A\u7684\u8857\u4E0A\u904A\u8569\u3002<br> \u4F46\u662F\uFF0C\u6490\u8457\u67FA\u6756\uFF0C\u5C31\u7B97\u6211\u51FA\u8072\uFF0C\u4E5F\u6C92\u6709\u5973\u5B69\u80AF\u966A\u6211\u3002<br> \u5C31\u90A3\u6A23\uFF0C\u6709\u5929\uFF0C\u6211\u5C31\u50CF\u5E73\u6642\u90A3\u6A23\u65E9\u4E0A\u6B78\u5BB6\uFF0C\u5927\u767D\u5929\u6642\u9192\u4F86\uFF0C<br> \u5C31\u662F\u90A3\u5929\u2026\u2026\u6211\u67D0\u500B\u4E09\u5E74\u6C92\u898B\u7684\u670B\u53CB\u4F86\u62DC\u8A2A\u6211\u3002<br> \u4ED6\u4E00\u898B\u5230\u6211\uFF0C\u5C31\u8AAA\uFE55\u300C\u5C3E\u5D0E\uFF0C\u4F60\u4E5F\u6C92\u6709\u8B8A\u5462\uFF01\u300D<br> \u90A3\u50A2\u4F19\u2026\u8981\u8AAA\u90A3\u50A2\u4F19\u7684\u904E\u53BB\uFF0C\u4ED6\u4EE5\u524D\u662F\u98DB\u8ECA\u9EE8\uFF0C\u982D\u9AEE\u5168\u91D1\u8272\u7684\uFF0C<br> \u4F46\u73FE\u5728\u4ED6\u2026\u55EF\u2026\u76F4\u5230\u6700\u8FD1\u4ED6\u597D\u50CF\u9084\u6709\u67D3\u9AEE\u2026\u2026\u9AEE\u8173\u9084\u6709\u4E00\u9EDE\u5561\u8272\uFF0C\u4F46\u5176\u9918\u5168\u90FD\u8B8A\u6210\u9ED1\u8272\u4E86\u3002\u4ED6\u53C8\u5C0D\u6211\u8AAA\uFE55<br> \u300C\u6211\u5440\uFF0C\u5176\u5BE6\u5DF2\u7576\u4E86\u7238\u7238\u2026\u2026\u5DF2\u7D93\u4E0D\u80FD\u50CF\u6614\u65E5\u822C\u505A\u51FA\u90A3\u7A2E\u58DE\u4E8B\u5462\u3002<br> \u4E0D\u80FD\u50CF\u5C3E\u5D0E\u4F60\u90A3\u822C\u8AC7\u751A\u9EBC\u611B\u548C\u5922\u60F3\u2026\u2026\u7B97\u4E86\uFF0C\u56E0\u70BA\u518D\u600E\u9EBC\u8AAA\uFF0C\u6211\u4E5F\u6709\u5BB6\u5EAD\u4E86\u3002\u300D<br> \u4ED6\u9019\u6A23\u5C0D\u6211\u8AAA\u3002\u65BC\u662F\u6211\u5C31\u5C0D\u4ED6\u8AAA\uFE55<br> \u300C\u6211\u660E\u767D\u4E86\u3002\u4F60\u548C\u6211\u5C31\u4EE5\u5FE0\u65BC\u81EA\u5DF1\u7684\u65B9\u5F0F\u751F\u5B58\u5427\u2026\u2026<br> \u4EE5\u5FE0\u65BC\u81EA\u5DF1\u7684\u65B9\u5F0F\u751F\u5B58\u4E00\u5B9A\u662F\u6700\u597D\u7684\u3002<br> \u606D\u559C\u4F60\uFF0C\u9084\u6709\u2026\u2026Say-goodbye\u3000Scrap Alley\u2026\u2026\u300D<br> One! Two! Three! Four!</p><br><h3 id="\u30CF\u30A4\u30B9\u30AF\u30FC\u30EB-rock-n-roll" tabindex="-1">\u30CF\u30A4\u30B9\u30AF\u30FC\u30EB\u3000Rock&#39;n&#39;Roll <a class="header-anchor" href="#\u30CF\u30A4\u30B9\u30AF\u30FC\u30EB-rock-n-roll" aria-hidden="true">#</a></h3><blockquote><p>\u4FFA\u304C\u307E\u3060\u3001\u9AD8\u6821\u751F\u3060\u3063\u305F\u9803\u306E\u8A71\u3092\u805E\u3044\u3066\u304F\u308C\u308B\u304B\u30FC\u3044\uFF01\uFF01<br> \u305D\u308C\u306F\u4FFA\u304C\u307E\u3060\u9AD8\u6821\uFF11\u5E74\u306E\u9803\u306E\u4E8B\u306A\u3093\u3060\u3002<br> \u4FFA\u306B\u306F\u3042\u3093\u307E\u308A\u53CB\u9054\u304C\u3044\u306A\u304F\u3063\u3066\u3001\u3044\u3064\u3082\u4F11\u307F\u6642\u9593\u306B\u306A\u308B\u3068<br> \u3053\u3046\u3084\u3063\u3066\u3001\u4E00\u4EBA\u3067\u81EA\u5206\u306E\u5E2D\u306B\u5EA7\u3063\u3066\u30FB\u30FB\u30FB\u30FB\u30FB\u30FB\u30FB\u3000<br> \u30A6\u30A9\u30FC\u30AF\u30DE\u30F3\u3092\u8074\u3044\u3066\u3044\u305F\u3093\u3060\uFF01\uFF01\u3000<br> \u30C6\u30FC\u30D7\u306E\u4E2D\u8EAB\u306F\u4F8B\u3048\u3070\u3001\u30D6\u30EB\u30FC\u30B9\u30FB\u30B9\u30D7\u30EA\u30F3\u30B0\u30B9\u30C6\u30A3\u30FC\u30F3\u3084\uFF01\u30B8\u30E3\u30AF\u30BD\u30F3\u30FB\u30D6\u30E9\u30A6\u30F3\u3084\uFF01<br> \u4F50\u91CE\u5143\u6625\u3084\uFF01\u6D5C\u7530\u7701\u543E\u306A\u3093\u304B\u3092\u597D\u3093\u3067\u8074\u3044\u3066\u3044\u305F\u3093\u3060\u305C\u3063\uFF01\uFF01\u3000\u3000<br> \u305D\u3093\u306A\u98A8\u306B\u4FFA\u306F\u4F11\u307F\u6642\u9593\u4E00\u4EBA\u3067\u3044\u3064\u3082\u60B6\u3005\u3068\u3057\u3066\u3044\u305F\u3063\uFF01\uFF01<br> \u305D\u3093\u306A\u4FFA\u3092\u898B\u308B\u3068\u3001\u30AF\u30E9\u30B9\u306E\u9023\u4E2D\u306F\u307F\u30FC\u3093\u306A\u3001\u307F\u30FC\u3093\u306A\u3001\u307F\u30FC\u3093\u306A\u3063\u767D\u3044\u76EE\u3067\u898B\u3084\u304C\u3063\u305F\u3063\uFF01\uFF01<br> \u305D\u308C\u3067\u3082\u4FFA\u306F\u3001\u8CA0\u3051\u306A\u304B\u3063\u305F\u3041\uFF5E\uFF01\uFF01<br> \u4F11\u307F\u6642\u9593\u3082\u7D42\u308F\u308A\u306E\u65B9\u306B\u306A\u3063\u3066\u304D\u3066\u4FFA\u306F\u6B21\u306E\u6388\u696D\u306E\u652F\u5EA6\u3092\u3057\u3066\u3044\u305F\u3093\u3060\u3002<br> \u3059\u308B\u3068\u3001\u4ECA\u5EA6\u306F\u3055\u3063\u304D\u3088\u308A\u3082\u3001\u3082\u3063\u3068\u3001\u3082\u3063\u3068\u3001\u3082\u3063\u3063\u3068\u5927\u52E2\u3067\u3001<br> \u4FFA\u306E\u3053\u3068\u767D\u3044\u76EE\u3067\u898B\u3066\u3044\u3084\u304C\u3063\u305F\u3002<br> \u305D\u308C\u3067\u3082\u4FFA\u306F\u3001\u8CA0\u3051\u306A\u304B\u3063\u305F\u3041\uFF5E\uFF01\uFF01\uFF01<br> \u3042\u308B\u4E00\u4EBA\u306E\u5C11\u5E74\u304C\u4FFA\u306E\u65B9\u306B\u8FD1\u3065\u3044\u3066\u304D\u3066\u3053\u3046\u8A00\u3063\u305F\u3093\u3060\u3000<br> \u300C\u306D\u3047\u306D\u3047\u5C3E\u5D0E\u541B\u3001\u306A\u3093\u3067\u305D\u3093\u306A\u306B\u6012\u3063\u3066\u308B\u306E\uFF1F\u300D\u3000<br> \u3060\u3041\u304B\u3089\u4FFA\u306F\u8A00\u3063\u3066\u3084\u3063\u305F\u305C\u3047\uFF01<br> \u300C\u3088\u304F\u8074\u3051\u3063\uFF01\u3053\u308C\u304C\u30ED\u30C3\u30AF\u30F3\u30ED\u30FC\u30EB\u3060\u3041\uFF01\uFF01\uFF01\u300D</p></blockquote><p>\u807D\u807D\u6211\u9084\u662F\u4E00\u500B\u9AD8\u4E2D\u751F\u6642\u7684\u6545\u4E8B\uFF0C\u597D\u55CE\uFF1F<br> \u90A3\u662F\u6211\u9084\u662F\u9AD8\u4E2D\u4E00\u5E74\u751F\u6642\u7684\u4E8B\u3002<br> \u6211\u670B\u53CB\u4E0D\u591A\uFF0C\u7D93\u5E38\u4E00\u5230\u4F11\u606F\u6642\u9593\uFF0C\u6211\u5C31\u6703\u5750\u5728\u81EA\u5DF1\u7684\u5EA7\u4F4D\u4E0A\u2026\u2026<br> \u7368\u500B\u807DWalkman\uFF01<br> \u9304\u97F3\u5E36\u88E1\u9762\uFF0C\u6703\u6709Bruce Springsteen\uFF01Jackson Browne\uFF01\u4F50\u91CE\u5143\u6625\uFF01\u6D5C\u7530\u7701\u543E\u7B49\u6211\u559C\u6B61\u7684\u6B4C\u624B\u7684\u6B4C\uFF01<br> \u6211\u5C31\u662F\u9019\u6A23\u5728\u4F11\u606F\u6642\u9593\u4E00\u500B\u4EBA\u60B6\u60B6\u7684\uFF01<br> \u4E00\u898B\u5230\u6211\u9019\u6A23\uFF0C\u73ED\u88E1\u7684\u4E00\u9EE8\u4EBA\u5168\u90FD\u3001\u5168\u90FD\u3001\u5168\u90FD\u5411\u6211\u5831\u4EE5\u767D\u773C\uFF01<br> \u5118\u7BA1\u5982\u6B64\uFF0C\u6211\u4E26\u6C92\u6709\u8A8D\u8F38\uFF01<br> \u4F11\u606F\u6642\u9593\u5FEB\u8981\u5B8C\u7684\u6642\u5019\uFF0C\u6211\u5C31\u70BA\u4E0B\u4E00\u5802\u4F5C\u6E96\u5099\u3002\u4E00\u7576\u6211\u9019\u6A23\u505A\uFF0C\u4ECA\u6B21\uFF0C\u4ED6\u5011\u6BD4\u525B\u624D\u66F4\u52A0\u3001\u66F4\u52A0\u3001\u66F4\u52A0\u3001\u66F4\u52A0\u5F37\u70C8\u5730\u5411\u6211\u5831\u4EE5\u767D\u773C\u3002<br> \u5118\u7BA1\u5982\u6B64\uFF0C\u6211\u4E26\u6C92\u6709\u8A8D\u8F38\uFF01<br> \u6709\u4E00\u500B\u5C11\u5E74\u8D70\u8FD1\u6211\uFF0C\u9019\u6A23\u5C0D\u6211\u8AAA\uFE55\u300C\u5582\u5582\uFF0C\u5C3E\u5D0E\uFF0C\u5E79\u561B\u90A3\u9EBC\u751F\u6C23\uFF1F\u300D<br> \u65BC\u662F\u6211\u5C31\u5C0D\u4ED6\u8AAA\uFE55<br> \u300C\u7D66\u6211\u597D\u597D\u807D\u6E05\u695A\uFF0C\u9019\u5C31\u662FRock&#39;n&#39;Roll\uFF01\u300D</p><br><h3 id="freeze-moon" tabindex="-1">Freeze Moon <a class="header-anchor" href="#freeze-moon" aria-hidden="true">#</a></h3><blockquote><p>\u4ECA\u591C\u306F\u672C\u5F53\u306B\u3069\u3046\u3082\u3042\u308A\u304C\u3068\u3046\uFF01\uFF01\u305D\u308C\u3058\u3083\u6700\u5F8C\u306B\u3001\u3082\u3046\u4E00\u66F2Rock&#39;n&#39;Roll\u3092\u3084\u308D\u3046\u305C\uFF01\uFF01<br> \u30EF\u30F3\u30FB\u30C8\u30A5\u30FC\u30FB\u30B9\u30EA\u30FC\u30D5\u30A9\u30FC\uFF01\uFF01</p></blockquote><p>\u4ECA\u591C\u771F\u7684\u5F88\u611F\u8B1D\u5927\u5BB6\uFF01\uFF01 \u90A3\u9EBC\uFF0C\u518D\u4F86\u4E00\u66F2Rock&#39;n&#39;Roll\u5427\uFF01\uFF01One two three four\uFF01</p><br><hr><blockquote><p>\u4ECA\u65E5\u306F\u672C\u5F53\u306B\u3069\u3046\u3082\u3042\u308A\u304C\u3068\u3046\u3002\u4ECA\u65E5\u4FFA\u305F\u3061\u304C\u3053\u3053\u306B\u30FB\u30FB\u3053\u3053\u307E\u3067\u3053\u3046\u3057\u3066\u3053\u308C\u305F\u306E\u306F\u3001\u3053\u306EHeart of Klaxon\u3068\u305D\u3057\u3066\u305F\u304F\u3055\u3093\u306E\u30B9\u30BF\u30C3\u30D5\u306E\u304A\u304B\u3052\u3067\u3059\u30FB\u30FB\u30FB\u30FB\u30FB\u3002<br> \u4FFA\u304C\u306F\u3058\u3081\u3066\u30AE\u30BF\u30FC\u3092\u624B\u306B\u3057\u305F\u306E\u306F\u5C0F\u5B66\u6821\uFF16\u5E74\u751F\u306E\u3068\u304D\u3060\u3063\u305F\u3001\u305D\u306E\u3068\u304D\u306F\u767B\u6821\u62D2\u5426(\u3068\u3046\u3053\u3046\u304D\u3087\u3072)\u3092\u3057\u3066\u3044\u3066\u30FB\u30FB\u30FB\u30FB\u30FB\u30FB\u305D\u3093\u306A\u4FFA\u304C\u4FFA\u306E\u4E8B\u3092\u3053\u3093\u306A\u306B\u6E29\u304B\u304F\u8FCE\u3048\u3066\u304F\u308C\u308B\u306A\u3093\u3066\u601D\u3063\u3066\u3082\u307F\u306A\u304B\u3063\u305F\u3002<br> \u4ECA\u591C\u306E\u4FFA\u305F\u3061\u3092\u3053\u3093\u306A\u306B\u6E29(\u3042\u305F\u305F)\u304B\u304F\u8FCE\u3048\u3066\u304F\u308C\u305F\u3053\u3068\u3092\u30E1\u30F3\u30D0\u30FC\u3084\u30B9\u30BF\u30C3\u30D5\u4E00\u540C\u672C\u5F53\u306B\u611F\u8B1D\u3057\u307E\u3059\u3001\u672C\u5F53\u306B\u3069\u3046\u3082\u3042\u308A\u304C\u3068\u3046\u3002<br> \u30FB\u30FB\u30FB\u30FB\u307E\u305F\u3044\u3064\u304B\u3001\u307F\u3093\u306A\u306B\u4F1A\u3044\u305F\u3044\u3067\u3059\u3002<br> \u30FB\u30FB\u30FB\u30FB\u305D\u308C\u307E\u3067\u5143\u6C17\u3067\u3001\u3055\u3088\u3046\u306A\u3089\u30FB\u30FB\u30FB\u30FB</p></blockquote><p>\u4ECA\u5929\u771F\u7684\u975E\u5E38\u611F\u8B1D\u5927\u5BB6\u3002\u4ECA\u5929\u6211\u5011\u80FD\u5920\u5728\u6B64\u2026\u9019\u6A23\u805A\u96C6\u5728\u4E00\u8D77\uFF0C\u662F\u5168\u9760\u9019\u968AHeart of Klaxon\u4EE5\u53CA\u8A31\u591A\u7684\u5DE5\u4F5C\u4EBA\u54E1\u2026\u2026\u3002<br> \u6211\u521D\u6B21\u63A5\u89F8\u7D50\u4ED6\u662F\u5C0F\u5B786\u5E74\u7D1A\u7684\u6642\u5019\uFF0C\u90A3\u6642\u6211\u7D93\u5E38\u9003\u5B78\u2026\u2026\u90A3\u6A23\u7684\u6211\uFF0C\u7ADF\u7136\u6703\u53D7\u5230\u5927\u5BB6\u9019\u9EBC\u6EAB\u6696\u5730\u6B61\u8FCE\uFF0C\u6211\u771F\u7684\u6C92\u60F3\u904E\u3002<br> \u4ECA\u591C\u5927\u5BB6\u9019\u6A23\u6EAB\u6696\u5730\u6B61\u8FCE\u6211\u5011\uFF0C\u6211\u548C\u6240\u6709\u968A\u54E1\u548C\u5DE5\u4F5C\u4EBA\u54E1\u771F\u7684\u5F88\u611F\u8B1D\u5927\u5BB6\u3002<br> \u2026\u2026\u5E0C\u671B\u67D0\u5929\u518D\u898B\u5427\uFF01<br> \u2026\u2026\u5728\u90A3\u4E4B\u524D\u5927\u5BB6\u8981\u4FDD\u91CD\u554A\uFF0C\u518D\u898B\u2026\u2026</p><hr><p>\u8F49\u8F09\u81EA:</p><ul><li><a href="https://web.archive.org/web/20110501074524/http://blog.yam.com/forgetnot/category/634985/page=2" target="_blank" rel="noopener noreferrer">1985\u5E748\u670825\u65E5 \u5927\u962A\u7403\u5834</a></li></ul>',38),i=[o];function t(c,n,h,p,d,s){return b(),e("div",null,i)}var k=r(a,[["render",t]]);export{_ as __pageData,k as default};
