import{_ as r,c as b,o as e,a as t}from"./app.d0234d09.js";const u='{"title":"1991/10/02-03 - \u5927\u962A\u57CE\u30DB\u30FC\u30EB","description":"","frontmatter":{},"headers":[],"relativePath":"Live/19911002.md","lastUpdated":1668047154609}',a={},o=t('<h1 id="_1991-10-02-03-\u5927\u962A\u57CE\u30DB\u30FC\u30EB" tabindex="-1">1991/10/02-03 - \u5927\u962A\u57CE\u30DB\u30FC\u30EB <a class="header-anchor" href="#_1991-10-02-03-\u5927\u962A\u57CE\u30DB\u30FC\u30EB" aria-hidden="true">#</a></h1><p>\u9019\u662F\u8FD1\u65E5\u5F97\u5230\u7684\u5BF6\u8CB4\u97F3\u8072\u6A94\uFF0C\u662F\u4E00\u4F4D\u65E5\u672C\u7DB2\u53CB\u7576\u5E74\u5728OZAKI\u7684\u6F14\u5531\u6703\u73FE\u5834\u5077\u5077\u9304\u4E0B\u7684\uFF0C\u662F\u4E00\u6BB5\u4EE4\u4EBA\u611F\u52D5\u7684MC\uFF0C\u5E0C\u671B\u5927\u5BB6\u6703\u559C\u6B61\uFF01\u5C31\u5982OZAKI\u6240\u8AAA\uFF0C\u307F\u3093\u306A\u9811\u5F35\u308D\u3046\u306D\uFF0C\u5927\u5BB6\u52A0\u6CB9\u554A\uFF01</p><hr><blockquote><p>\u4E8C\u5341\u6B73\u306E\u6642\u306B\u30B9\u30C6\u30FC\u30B8\u3092\u964D\u308A\u305F\u3002<br> \u5927\u962A\u3067\u306F\u5927\u962A\u7403\u5834\u3092\u3084\u3063\u305F\u3053\u3068\u304C\u3042\u308B\u3002<br> \uFF13\u5E74\u9593\u306E\u3042\u3044\u3060\u306B\u3001\u30E9\u30A4\u30D6\u30CF\u30A6\u30B9\u304B\u3089\u56DE\u3063\u3066\u5927\u962A\u7403\u5834\u3092\u3084\u308B\u307E\u3067\u306E\u3001\u305D\u306E\u8208\u596E\u3063\u3066\u3044\u3063\u305F\u3044\u306A\u3093\u3060\u3063\u305F\u3093\u3060\u3002<br> \u3060\u3051\u3069\uFF13\u5E74\u9593\u3084\u3063\u3066\u308B\u3042\u3044\u3060\u306B\u3001<br> \u5927\u962A\u7403\u5834\u3092\u3084\u308A\u3001\u4EE3\u3005\u6728\u306E\u30AA\u30EA\u30F3\u30D4\u30C3\u30AF\u30D7\u30FC\u30EB\u3092\uFF12\u65E5\u9593\u3082\u51FA\u6765\u308B\u3088\u3046\u306B\u306A\u3063\u3066\u3001<br> \u3044\u3063\u305F\u3044\u81EA\u5206\u304C\u4F55\u3092\u4F1D\u3048\u3066\u3044\u308B\u306E\u304B\u3001\u4F55\u3092\u6B4C\u3063\u3066\u3044\u308B\u306E\u304B\u3001\u5206\u304B\u3089\u306A\u304F\u306A\u3063\u305F\u3093\u3060\u3002<br> \u305D\u308C\u3067\u50D5\u306F\u30B9\u30C6\u30FC\u30B8\u3092\u964D\u308A\u308B\u3053\u3068\u306B\u3057\u305F\u3002<br> \u305D\u308C\u304B\u3089\u3001\u4ECA\u65E5\u3068\u8A00\u3046\u65E5\u307E\u3067\u3001\u63A2\u3057\u7D9A\u3051\u3066\u304D\u305F\u3002<br> \u6642\u306B\u306F\u3001\u541B\u305F\u3061\u3092\u3001\u88CF\u5207\u308B\u69D8\u306A\u3053\u3068\u3092\u3057\u305F\u304B\u3082\u77E5\u308C\u306A\u3044\u3002<br> \u305D\u3057\u3066\u3001\u50D5\u3082\u7CBE\u4E00\u676F\u3001\u771F\u5B9F\u3092\u4F1D\u3048\u308B\u70BA\u306B\u52AA\u529B\u30FB\u30FB\u30FB\u3000\u547D\u304C\u3051\u3067\u30FB\u30FB\u30FB<br> \u81EA\u5206\u3067\u8A00\u3046\u306E\u3082\u5909\u306A\u3093\u3060\u3051\u3069\u3082\u30FB\u30FB\u30FB</p><p>\u30CB\u30E5\u30FC\u30E8\u30FC\u30AF\u3067\u306E\u66AE\u3089\u3057\u306E\u3053\u3068\u3001\u65B0\u3057\u304F\u4F5C\u3063\u305F\u30D1\u30F3\u30D5\u30EC\u30C3\u30C8\u306E\u4E2D\u306B\u3082\u5C11\u3057\u3060\u3051\u66F8\u3044\u3066\u898B\u305F\u3002<br> \u3082\u3057\u826F\u304B\u3063\u305F\u3089\u3001\u8AB0\u304B\u3001\u307F\u3093\u306A\u3067\u56DE\u3057\u8AAD\u307F\u3057\u3066\u3082\u3044\u3044\u3057\u3001\u5C11\u3057\u3060\u3051\u3067\u3082\u3044\u3044\u304B\u3089\u3001<br> \u50D5\u306E\u6C17\u6301\u3061\u3092\u5206\u304B\u3063\u3066\u6B32\u3057\u3044\u3068\u601D\u3046\u3002<br> \u30B9\u30C6\u30FC\u30B8\u3092\u964D\u308A\u308B\u524D\u306B\u50D5\u306F\u3053\u3046\u8A00\u3063\u305F\u3002<br> \u50D5\u306B\u3064\u3044\u3066\u3053\u3044\u3002\u3064\u3044\u3066\u304F\u308C\u3070\u305D\u3053\u306B\u7B54\u3048\u304C\u3042\u308B\u3002<br> \u305D\u3046\u8A00\u3063\u3066\u30CB\u30E5\u30FC\u30E8\u30FC\u30AF\u306B\u884C\u3063\u305F\u3002<br> \u30CB\u30E5\u30FC\u30E8\u30FC\u30AF\u306B\u3044\u3063\u3066\u308B\u9593\u306B\u6CA2\u5C71\u306E\u97F3\u697D\u304C\u751F\u307E\u308C\u3066\u3044\u305F\u3002<br> \u305D\u308C\u306F\u50D5\u306B\u3068\u3063\u3066\u3068\u3066\u3082\u3046\u308C\u3057\u3044\u3053\u3068\u3060\u3063\u305F\u3002<br> \u4E00\u3064\u306E\u97F3\u697D\u3058\u3083\u306A\u304F\u3066\u3001\u6CA2\u5C71\u306E\u97F3\u697D\u306B\u307F\u3093\u306A\u304C\u51FA\u4F1A\u3063\u3066\u308B\u3063\u3066\u3053\u3068\u304C\u3001\u50D5\u306B\u306F\u3046\u308C\u3057\u304B\u3063\u305F\u3002<br> \u9055\u3063\u305F\u60B2\u3057\u307F\u3082\u3042\u3063\u305F\u3051\u3069\u30FB\u30FB<br> \u305D\u3057\u3066\u7B54\u3048\u3063\u3066\u306E\u306F\u3001\u304D\u3063\u3068\u3053\u3093\u306A\u3053\u3068\u3060\u308D\u3046\u3068\u601D\u3046\u3002<br> \u4EBA\u751F\u306F\u81EA\u5206\u306E\u3057\u305F\u3044\u3053\u3068\u3092\u3001\u3059\u308B\u70BA\u306B\u3042\u308B\u3093\u3060\u3002<br> \u611B\u3055\u308C\u305F\u3044\u304B\u3089\u4EBA\u3092\u611B\u3057\u3001\u611B\u3057\u3059\u304E\u3066\u8F9B\u304F\u306A\u308B\u3053\u3068\u3082\u3042\u308B\u3002<br> \u3060\u3051\u3069\u7B54\u3048\u306F\u3001\u541B\u3092\u611B\u3057\u3066\u308B\u3002\u541B\u3092\u611B\u3057\u305F\u307E\u307E\u306E\u59FF\u3067\u3001\u3082\u3046\u4E00\u5EA6\u3053\u3046\u3084\u3063\u3066\u3001\u30B9\u30C6\u30FC\u30B8\u306E\u4E0A\u304B\u3089<br> \u541B\u305F\u3061\u306B\u4F1A\u3044\u305F\u304B\u3063\u305F\u3002<br> \u3053\u308C\u304C\u50D5\u306E\u7B54\u3048\u306A\u3093\u3060\u3002<br> \u672C\u5F53\u306B\u307E\u305F\u4F1A\u3048\u3066\u3088\u304B\u3063\u305F\u3002<br> \u4ECA\u65E5\u306F\u672C\u5F53\u306B\u3069\u3046\u3082\u3042\u308A\u304C\u3068\u3046\u3002</p><p>\u30CB\u30E5\u30FC\u30E8\u30FC\u30AF\u3067\u899A\u3048\u305F\u3068\u3063\u3066\u3082\u7D20\u6575\u306A\u8A00\u8449\u304C\u3042\u308B\u3002<br> You can not miss it.<br> \u3064\u307E\u308A\u3001\u7D76\u5BFE\u5927\u4E08\u592B\u3055\uFF01\u305D\u3046\u8A00\u3046\u610F\u5473\u306A\u3093\u3060\u3051\u3069\u3001<br> \u3064\u307E\u308A\u4FFA\u304C\u8A00\u3044\u305F\u3044\u306E\u306F\u3001\u307F\u3093\u306A\u9811\u5F35\u308D\u3046\u306D\u3002</p><p>\u3042\u308A\u304C\u3068\u3046\u3002</p></blockquote><p>\u5728\u4E8C\u5341\u6B72\u7684\u6642\u5019\u6211\u96E2\u958B\u4E86\u821E\u53F0\u3002 \u6211\u66FE\u5728\u5927\u962A\u7684\u5927\u962A\u7403\u5834\u958B\u6F14\u5531\u6703\u3002<br> \u5728\u90A3\u4E09\u5E74\u9593\uFF0C\u6211\u7531LIVE HOUSE\u958B\u59CB\u5230\u8655\u6F14\u5531\uFF0C\u76F4\u5230\u5728\u5927\u962A\u7403\u5834\u6F14\u5531\uFF0C\u90A3\u7A2E\u8208\u596E\u5230\u5E95\u662F\u751A\u9EBC\u56DE\u4E8B\u5462\uFF1F<br> \u4F46\u662F\uFF0C\u5728\u90A3\u6F14\u5531\u7684\u4E09\u5E74\u9593\uFF0C\u7576\u6211\u958B\u59CB\u80FD\u5728\u5927\u962A\u7403\u5834\u6F14\u5531\uFF0C\u80FD\u5728\u4EE3\u4EE3\u6728\u7684OLIMPIC POOL\u9023\u7E8C\u5169\u65E5\u6F14\u5531\u6642\uFF0C<br> \u6211\u537B\u8B8A\u5F97\u4E0D\u77E5\u9053\u81EA\u5DF1\u5230\u5E95\u5728\u50B3\u9054\u751A\u9EBC\uFF0C\u5728\u5531\u8457\u751A\u9EBC\u3002<br> \u56E0\u6B64\uFF0C\u90A3\u6642\u6211\u6C7A\u5B9A\u96E2\u958B\u821E\u53F0\u3002<br> \u5728\u90A3\u4E4B\u5F8C\uFF0C\u6211\u4E00\u76F4\u5728\u5C0B\u627E\u7B54\u6848\uFF0C\u76F4\u5230\u4ECA\u5929\u3002<br> \u4E5F\u8A31\uFF0C\u6709\u4E9B\u6642\u5019\u6211\u505A\u4E86\u4E00\u4E9B\u8F9C\u8CA0\u4E86\u4F60\u5011\u7684\u4E8B\u4E5F\u8AAA\u4E0D\u5B9A\u3002<br> \u800C\u6211\u4E5F\u70BA\u4E86\u50B3\u9054\u771F\u5BE6\u800C\u52AA\u529B\u2026\u2026\u62DA\u547D\u2026\u2026<br> \u96D6\u7136\u7531\u81EA\u5DF1\u9019\u6A23\u8AAA\u662F\u6709\u9EDE\u5947\u602A\u2026\u2026</p><p>\u6709\u95DC\u5728\u7D10\u7D04\u7684\u751F\u6D3B\uFF0C\u6211\u8A66\u8457\u5BEB\u4E86\u4E00\u9EDE\u5728\u65B0\u5F04\u7684\u5834\u520A\u4E2D\u3002<br> \u82E5\u53EF\u4EE5\u7684\u8A71\uFF0C\u5927\u5BB6\u5230\u8655\u50B3\u95B1\u4E5F\u53EF\u4EE5\uFF0C\u6211\u5E0C\u671B\u5927\u5BB6\u80FD\u591A\u5C11\u4E86\u89E3\u4E00\u9EDE\u6211\u7684\u5FC3\u60C5\u3002<br> \u5728\u96E2\u958B\u821E\u53F0\u524D\uFF0C\u6211\u66FE\u9019\u6A23\u8AAA\uFE55<br> \u300C\u8DDF\u96A8\u6211\u5427\uFF01\u8DDF\u96A8\u6211\u7684\u8A71\u5C31\u6703\u627E\u5230\u7B54\u6848\u3002\u300D<br> \u7136\u5F8C\u6211\u4FBF\u53BB\u4E86\u7D10\u7D04\u3002<br> \u5728\u7D10\u7D04\u671F\u9593\uFF0C\u7522\u751F\u4E86\u8A31\u591A\u97F3\u6A02\u3002<br> \u90A3\u5C0D\u6211\u800C\u8A00\u662F\u975E\u5E38\u9AD8\u8208\u7684\u4E8B\u3002<br> \u5927\u5BB6\u4E26\u4E0D\u662F\u53EA\u63A5\u89F8\u4E00\u7A2E\u97F3\u6A02\uFF0C\u800C\u662F\u9047\u4E0A\u8A31\u591A\u4E0D\u540C\u7684\u97F3\u6A02\uFF0C\u5C0D\u6B64\u6211\u5F88\u9AD8\u8208\u3002<br> \u96D6\u7136\u4E5F\u6709\u72AF\u932F\u7684\u60B2\u54C0\u2026\u2026<br> \u800C\u6211\u60F3\uFF0C\u6240\u8B02\u7684\u7B54\u6848\uFF0C\u4E00\u5B9A\u5C31\u662F\u9019\u9EBC\u56DE\u4E8B\u5427\uFF01<br> \u4EBA\u751F\u662F\u70BA\u4E86\u505A\u81EA\u5DF1\u60F3\u505A\u7684\u4E8B\u800C\u5B58\u5728\u7684\u3002<br> \u56E0\u70BA\u60F3\u88AB\u611B\u800C\u611B\u4EBA\uFF0C\u96D6\u7136\u5076\u723E\u6703\u56E0\u70BA\u611B\u5F97\u592A\u6DF1\u800C\u75DB\u82E6\uFF0C<br> \u4F46\u662F\uFF0C\u7B54\u6848\u4ECD\u662F\u300C\u611B\u4F60\u300D\u3002\u6211\u4E00\u76F4\u5E0C\u671B\uFF0C\u7DAD\u6301\u611B\u4F60\u6642\u7684\u6A21\u6A23\uFF0C\u518D\u4E00\u6B21\u9019\u6A23\u5728\u53F0\u4E0A\u8207\u4F60\u5011\u76F8\u6703\u3002<br> \u9019\u5C31\u662F\u6211\u7684\u7B54\u6848\u3002<br> \u80FD\u518D\u6B21\u898B\u9762\uFF0C\u771F\u7684\u592A\u597D\u4E86\u3002<br> \u4ECA\u5929\u771F\u7684\u5F88\u611F\u8B1D\u5927\u5BB6\uFF01</p><p>\u5728\u7D10\u7D04\u6211\u5B78\u61C2\u4E86\u4E00\u53E5\u975E\u5E38\u6709\u610F\u601D\u7684\u8AAA\u8A71\u3002<br> You can not miss it.<br> \u63DB\u53E5\u8A71\u8AAA\uFF0C\u7D55\u5C0D\u6C92\u554F\u984C\u7684\uFF01\u96D6\u662F\u90A3\u7A2E\u610F\u601D\uFF0C<br> \u4F46\u6211\u60F3\u8AAA\u7684\u662F\u300C\u5927\u5BB6\u52A0\u6CB9\u554A\uFF01\u300D\u3002</p><p>\u8B1D\u8B1D\uFF01</p><hr><p>\u8F49\u8F09\u81EA:</p><ul><li><a href="https://web.archive.org/web/20110501074524/http://blog.yam.com/forgetnot/category/634985/page=1" target="_blank" rel="noopener noreferrer">1991\u5E74\u5927\u962A\u57CEHALL\u7684MC</a></li></ul>',11),p=[o];function _(n,s,c,i,d,l){return e(),b("div",null,p)}var f=r(a,[["render",_]]);export{u as __pageData,f as default};
